package chapter03;

public class SwapTest01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		
		
		System.out.println("a:" +a+", b:"+b);
		
		int save = a;
		a = b;
		b = save;
		
		System.out.println("a:" +a+", b:"+b);
		
	}

}
